function incrementar(index) {
  produtos.value[index].quantidade++
}
function descrementar(index) {
  console.log(produtos.value[index].quantidade)
if(produtos.value[index].quantidade ==0){
    alert("A quantidade de itens não pode ser negativa!!!") 
    return;
    
  }
  produtos.value[index].quantidade--
}
let valortotal = ref(0)

let carrinho = ref([])
function addcarrinho(item) {
  for(let a of carrinho.value){
    
    if(a.codigo == item.id){
      alert("Item já foi adicionado no carrinho!!!")
    return;

    }

  }
  carrinho.value.push({
    img: item.img,
    codigo: item.id,
    nome: item.nome,
    boboCoin: item.bolsoCoin,
    quantidade: item.quantidade,
    totalitem: item.bolsoCoin * item.quantidade
  })
  totalvalor()
  item.quantidade = 1
}
function totalvalor() {
  for (let contador = 0; contador < carrinho.value.length; contador++) {
    valortotal.value = valortotal.value + carrinho.value[contador].totalitem
  }
}
function limpacarrinho() {
  carrinho.value = []
}
function remover(index) {
  carrinho.value.splice(index, 1)
}
